import { bootstrapApplication } from '@angular/platform-browser';
import { provideRouter } from '@angular/router';
import { provideHttpClient, withInterceptors } from '@angular/common/http';
import { AppComponent } from './app/app.component';
import { LoginComponent } from './app/pages/login.component';
import { ProductsComponent } from './app/pages/products.component';
import { authInterceptor } from './app/shared/auth.interceptor';
import { AuthGuard } from './app/shared/auth.guard'; // función guard
// Rutas
const routes = [{
  path: '',
  redirectTo: 'products',
  pathMatch: 'full'
}, {
  path: 'login',
  component: LoginComponent
}, {
  path: 'products',
  component: ProductsComponent,
  canActivate: [AuthGuard]
}, {
  path: '**',
  redirectTo: 'products'
}];
// Bootstrap de la app
bootstrapApplication(AppComponent, {
  providers: [provideRouter(routes), provideHttpClient(withInterceptors([authInterceptor]))
  // No se agrega AuthGuard, es una función CanActivateFn
  // Todos los servicios usados en authInterceptor deben tener @Injectable({ providedIn: 'root' })
  ]
}).catch(err => console.error(err));
